INSERT INTO `sanpham` (`masanpham`, `tensanpham`, `gia`, `mota`, `hinhminhhoa`, `hinhchitiet`, `maloaisp`, `soluong`) VALUES
(1, 'Viết Chì 2B - 3404', 2430, 'Viết Chì 2B - 3404 được làm từ chất liệu gỗ bền chắc, ruột chì có độ cứng 2B phù hợp cho các bạn học sinh, sinh viên tập viết, vẽ hình.\r\n\r\nSản phẩm cho chất chì đen đậm, khi sử dụng sẽ tạo nên những nét chì rất mịn và sắc nét.\r\n\r\nChì có độ bền màu rất cao, không bị dây bẩn hoặc lem nhoè, lâu phai màu khi để lâu.\r\n\r\nRất dễ dàng tẩy sạch bằng gôm, đồng thời, ruột chì cũng rất ít bị gãy vụn khi vẽ hoặc chuốt, rất tiết kiệm, cho bạn sử dụng lâu dài.\r\n\r\nĐộ cứng: 2B', '/assets/images/butchi001_1.jpg', '/assets/images/butchi001_1.jpg', 1, 10),
(2, 'Viết chì 2B DOL-FC1661 - Xanh Lá', 11250, 'Viết chì 2B DOL-FC1661 - Xanh Lá là vật dụng không thể thiếu đối với các em học sinh, bút thích hợp cho việc học tập, vẽ tranh.\r\n\r\nThiết kế bút thon gọn, vừa tay, giúp bạn dễ dàng thao tác và viết chữ hơn.\r\n\r\nChất liệu gỗ bền chắc, không dễ bị gãy nứt, dễ gọt bỏ.\r\n\r\nNgòi bút 2B cho độ đậm chuẩn, dễ dàng viết chữ mà không cần phải ấn mạnh. Ngoài ra, ngòi bút còn để lại ít than chì trên giấy, không làm bẩn giấy tập, dễ dàng tẩy xóa.\r\n\r\nCấu tạo thân bút góc cạnh giúp người dùng cầm nắm chắc chắn và', '/assets/images/butchi002_1.jpg', '/assets/images/butchi002_2.jpg', 1, 20),
(3, 'Bút Chì 2B Thân Vàng Có Tẩy Marco 4200E2B-12CB', 4180, 'Bút Chì 2B Thân Vàng Có Tẩy Marco 4200E2B-12CB  là dụng cụ không thể thiếu của các bạn học sinh hay những bạn đang theo học vẽ. Ngòi bút chuẩn 2B thích hợp để viết, vẽ, phác họa tranh,... Bút cho chất chì mịn, sắc nét, khi sử dụng rất ít đổ bột chì, không lem nhoè và rất dễ tẩy sạch. Thân bút được thiết kế nhỏ gọn, cho bạn dễ dàng cầm nắm và điều chỉnh nét vẽ, đồng thời giúp người dùng dễ dàng bảo quản hoặc mang theo bên mình.\r\n\r\n- Chất liệu: Gỗ bền chắc, không dễ bị gãy nứt, dễ gọt bỏ.\r\n\r\n- Thiết kế:\r\n\r\n+ Kiểu dáng bút thon gọn, vừa tay, cho bạn viết chữ đẹp và thao tác linh hoạt hơn.\r\n\r\n+ Ngòi bút 2B cho độ đậm chuẩn, nét viết rõ ràng giúp bạn dễ dàng viết chữ mà không cần phải ấn mạnh.\r\n\r\n+ Bút được làm bằng gỗ chắc chắn, không bị vỡ nứt, dễ chuốt, an toàn cho sức khỏe người tiêu dùng.\r\n\r\n- Kích thước ngòi: 2B', '/assets/images/butchi003_1.jpg', '/assets/images/butchi003_2.jpg', 1, 30),
(4, 'Bút Chì 2B Unicorn/ SK-PC2B006', 4185, 'Bút Chì 2B Unicorn/ SK-PC2B006\r\n\r\nRuột chì chất lượng cao, gỗ mềm dễ chuốt\r\n\r\nThân có 3 màu pastel: hồng, tím, xanh lá, in hình Unicorn thiết kế riêng của bitex, thân tam giác dễ cằm chắc chắn.\r\n\r\nGôm 3 màu trùng với thân, chất lượng cao, tẩy sạch dễ dàng tạo sự tiện dụng\r\n\r\nHộp giấy thiết kế đặc trưng', '/assets/images/butchi004_1.jpg', '/assets/images/butchi004_2.jpg', 1, 10),
(5, 'Bút Chì 2B Dinosaur Pattern SK-PC2B009 - Màu Cam', 4185, 'Bút Chì 2B Dinosaur Pattern SK-PC2B009\r\n\r\n- Ruột chì chất lượng cao, gỗ mềm dễ chuốt\r\n\r\n- Thân có 3 màu pastel: hồng, vàng, xanh nhạt, in hình cat thiết kế riêng của bitex, thân tam giác dễ cằm chắc chắn.\r\n\r\n- Gôm 3 màu trùng với thân, chất lượng cao, tẩy sạch dễ dàng tạo sự tiện dụng.\r\n\r\n- An toàn, không độc hại', '/assets/images/butchi005_1.jpg', '/assets/images/butchi005_2.jpg', 1, 20),
(6, 'Bút Chì 2B Có Tẩy Thân Sọc Xanh Đỏ - Marco 4213E2B-12CB', 4512, 'được làm từ chất liệu gỗ bền chắc, không dễ bị gãy nứt. Ruột chì 2B cho độ đậm chuẩn, nét viết rõ ràng thích hợp để viết, vẽ, phác họa tranh,... Ðầu bút chì mềm mại giúp phác thảo nhanh và phù hợp cho các tác phẩm nghệ thuật.\r\n\r\n- Chất liệu: Gỗ bền chắc, không dễ bị gãy nứt, dễ gọt bỏ.\r\n\r\n- Thiết kế:\r\n\r\n+ Kiểu dáng bút thon gọn, vừa tay, cho bạn viết chữ đẹp và thao tác linh hoạt hơn.\r\n\r\n+ Ngòi bút 2B cho độ đậm chuẩn, nét viết rõ ràng giúp bạn dễ dàng viết chữ mà không cần phải ấn mạnh.\r\n\r\n+ Bút được làm bằng gỗ chắc chắn, không bị vỡ nứt, dễ chuốt, an toàn cho sức khỏe người tiêu dùng.\r\n\r\n- Kích thước ngòi: 2B', '/assets/images/butchi006_1.jpg', '/assets/images/butchi006_2.jpg', 1, 30),
(7, 'Bút Chì Đen Motif Giraffe Faber-Castell', 11160, 'Bút Chì Đen Motif Giraffe Faber-Castell\r\nNhững than viết chì được thuyết phục thông qua chất lượng khi viết. Thân hình tam giác và ruột chì chống gãy cao nhờ vào kỹ thuật SV bonding- lý tưởng trong công việc viết, soạn thảo, vẽ và phát hoạ trong trường học và văn phòng. \r\n\r\nKhông độc hại.\r\nThiết kế bút hình tam giác giúp trẻ cầm bút thoả mái.\r\nBút chì Motif 2B được thiết kế độc quyền, độc đáo và thú vị với hình các con vật dễ thương.\r\nKỹ thuật SV Bonding đặc biệt bảo vệ ruột chì chống gãy.\r\nVới 4 loại hình Motif: Bò sữa, Hưu cao cổ, Ong mật và Bọ rùa\r\nAn toàn cho trẻ em.', '/assets/images/butchi007_1.jpg', '/assets/images/butchi007_1.jp', 1, 10),
(8, 'Bút Bi Laris TL-095 - Mực Xanh - Thân Xanh', 8075, 'Bút Bi Laris TL-095 - Mực Xanh - Thân Xanh có thiết kế đột phá với cơ cấu bấm sáng tạo: Ruột tự động bật lên khi cài giắt bút vào túi áo.\r\n\r\nKiểu dáng hiện đại, phần tay cầm có đệm cao su mềm (grip) giúp cho cầm bút rất thoải mái.\r\n\r\nViết rất trơn, êm, mực ra đều, liên tục, đầu bút dạng needle và màu mực: 0.5mm.', '/assets/images/butbi008_1.jpg', '/assets/images/butbi008_2.jpg', 2, 10),
(9, 'Bút Bi Thiên Long TL-079 - Treeden - Xanh', 4085, 'Bút Bi Thiên Long TL-079 - Treeden - Xanh\r\n\r\nBút có thiết kế hiện đại, kết hợp hài hòa giữa phần thân dưới bằng nhựa trong nhỏ và thon dài với phần giắt bút màu nhựa trắng đục khỏe khoắn.Cảm giác cầm bút rất thoải mái, viết rất êm. Nét viết thanh mảnh và sắc nét.', '/assets/images/butbi009_1.jpg', '/assets/images/butbi009_2.jpg', 2, 30),
(10, 'Viết Bi Zebra F-301 Compact 0.7mm', 57340, 'Viết Bi Zebra F-301 Compact 0.7mm\r\n\r\nKim loại cao cấp, không gây kích ứng da. \r\n\r\nMực chất lượng, bền màu, không độc hại.\r\n\r\nThiết kế đẹp mắt, phù hợp với sinh viên và nhân viên văn phòng.\r\n\r\nCác đường rãnh gần tay cầm giúp cầm bút chắc tay, tránh trơn tuột.', '/assets/images/butbi010_1.jpg', '/assets/images/butbi010_2.jpg', 2, 30),
(11, 'Bút Bi Thiên Long TL-027 - Mực Xanh', 4085, 'Bút Bi Thiên Long TL-027 - Mực Xanh\r\n\r\nĐây là một trong những cây bút đang được học sinh sử dụng nhiều nhất tại Việt Nam. Bút có thiết kế tối giản, nhưng tinh tế và ấn tượng. Toàn bộ thân bút làm từ nhựa trong, phối hợp hoàn hảo với màu ruột bút bên trong.', '/assets/images/butbi011_1.jpg', '/assets/images/butbi011_2.jpg', 2, 30),
(12, 'Viết Bi Sarasa Clip JJ15-Xanh Dương', 27550, 'Viết Bi Sarasa Clip JJ15-Xanh Dương \r\n\r\nCó kiểu dáng hiện đại, vừa tay cầm, tạo sự thoải mái cho người sử dụng. Ngòi bút êm ái, mực ra đều không bị nhòe, cho chữ viết sắc nét và đẹp như mong muốn. Mực gel cao cấp, mau khô, không đóng cặn, không độc hại, an toàn cho sức khỏe.\r\n\r\nNgòi bút sản xuất theo công nghệ tiên tiến của Nhật nên viết êm, mực chảy đều, nét viết mềm mại\r\n\r\nCác phân tử mầu không hòa tan trong nước nên mực kháng nước và bền mầu. Mực ra đều, đậm, nhanh khô và không bị lem.\r\n\r\nPhần đệm tay được bao bọc bởi cao sụ mềm tạo cảo giác êm ái và không gây mỏi tay khi viết lâu.\r\n\r\nThân bút trong suốt cho phép bạn quan sát lượng mực còn lại trong ruột bút.\r\n\r\nGài bút thiết kế theo kiểu kẹp bướm, có thể điều chỉnh đề gài vào túi áo, bìa, tập sách…..\r\n\r\nBút có thiết kế đẹp mắt, nhiều mầu sắc cho bạn lựa chọn theo sở thích.', '/assets/images/butbi012_1.jpg', '/assets/images/butbi012_1.jpg', 2, 30),
(13, 'Bút Bi FO-03/VN - Mực Xanh', 3760, 'Bút Bi FO-03/VN - Mực Xanh là sản phẩm do Tập đoàn văn phòng phẩm Thiên Long sản xuất, mang nhãn hiệu FlexOffice. Bút FO-03 thiết kế trẻ trung , nhỏ gọn , dễ sử dụng . Sản phẩm được sản xuất theo công nghệ hiện đại, kiểu dáng phù hợp cho đối tượng học sinh, nhân viên văn phòng, tiểu thương, lao động phổ thông. Dạng bút bi cửa sổ bấm. Nút bấm và lò xo rất nhạy và bền, không bung, không kẹt, không tự rơi ra ngoài của sổ bấm. Tay cầm có hoa văn , giúp cầm bút không trơn , trượt khi viết. Mực không độc hại tiêu chuẩn quốc tế.', '/assets/images/butbi013_1.jpg', '/assets/images/butbi013_1.jpg', 2, 30),
(14, 'Bút Bi TL-08-Xanh', 4085, 'Đặc điểm:\r\n          Đầu bi: 0.8 mm, sản xuất tại Thụy Sĩ.\r\n          Bút bi dạng bấm cò.\r\n          Độ dài viết được: 1.200-1.500m.\r\n          Mực đạt tiêu chuẩn: ASTM D-4236, ASTM F 963-91, EN71/3, TSCA. \r\n\r\nLợi ích:\r\n          Đa dạng màu sắc rất tiện dụng phù hợp cho mọi người nên TL-08 là loại bút bi được khách hàng\r\n\r\n          tin tưởng sử dụng suốt 17 năm qua.\r\n          Thay ruột khi hết mực. ', '/assets/images/butbi014_1.jpg', '/assets/images/butbi014_1.jpg', 2, 30),
(15, 'Viết Máy Luyện 1011 - Thân Bút Màu Vàng', 36000, 'Viết Máy Luyện 1011 - Thân Bút Màu Vàng là bút cao cấp dành cho học sinh tiểu học. Bút giúp luyện chữ đẹp cho học sinh.\r\n\r\nMực trơn, ra đều, kiểu dáng đẹp, đa dạng', '/assets/images/butmay015_1.jpg', '/assets/images/butmay015_2.jpg', 3, 30),
(16, 'Viết Máy Gstar FP-02 - Màu Đỏ', 52200, 'Viết Máy Gstar FP-02\r\n\r\nThân bút bằng kim loại nhẹ, sơn tĩnh điện, rất thích hợp cho học sinh tiểu học.\r\n\r\nThân bút nhỏ gọn, trọng lượng nhẹ.\r\n\r\nPhần cán bút được thiết kế góc cạnh nhằm định vị tay cầm, giúp người cầm bút dễ dàng, không trơn tay khi viết.\r\n\r\nNgòi bút mài được cải tiến mang lại nét chữ thanh đậm dễ dàng, liền mạch mà không cần ấn mạnh tay.', '/assets/images/butmay016_1.jpg', '/assets/images/butmay016_1.jpg', 3, 30),
(17, 'Bút Máy Ngòi Mài - Eras E011 - Màu Hồng', 45000, 'Bút Máy Ngòi Mài - Eras - E011\r\n\r\nMẫu mã đẹp\r\n\r\nSử dụng viết nét thanh nét đậm\r\n\r\nTay cầm bằng nhựa, nhẹ, dễ sử dụng, phù hợp với học sinh lớp 1\r\n\r\nCó thể bơm mực hoặc sử dụng ống mực khô\r\n\r\nChất lượng tốt, giá cả hợp lý', '/assets/images/butmay017_1.jpg', '/assets/images/butmay017_2.jpg', 3, 30),
(18, 'Bút Máy FT-01/DO Plus Kèm Ngòi - Thân Bút Xanh Lá', 53200, 'Bút Máy FT-01/DO Plus Kèm Ngòi rất được các em học sinh yêu thích với thiết kể nổi bật in hình ảnh Doremon độc đáo, rất dễ thương.\r\n\r\nBút máy Thiên Long FT01/DO Plus với ngòi bút mài được cải tiến mang lại nét thanh, nét đậm dễ dàng, liền mạch mà không cần ấn mạnh tay, cùng thân bút nhỏ gọn, trọng lượng nhẹ, sẽ là người bạn đồng hành đáng tin cậy giúp các em học sinh có nét chữ đẹp và như ý muốn.', '/assets/images/butmay018_1.jpg', '/assets/images/butmay018_2.jpg', 3, 30),
(19, 'Viết Máy Gstar FP-02 - Xanh Dương', 52200, 'Viết Máy Gstar FP-02\r\n\r\nThân bút bằng kim loại nhẹ, sơn tĩnh điện, rất thích hợp cho học sinh tiểu học.\r\n\r\nThân bút nhỏ gọn, trọng lượng nhẹ.\r\n\r\nPhần cán bút được thiết kế góc cạnh nhằm định vị tay cầm, giúp người cầm bút dễ dàng, không trơn tay khi viết.\r\n\r\nNgòi bút mài được cải tiến mang lại nét chữ thanh đậm dễ dàng, liền mạch mà không cần ấn mạnh tay.', '/assets/images/butmay019_1.jpg', '/assets/images/butmay019_1.jpg', 3, 30),
(20, 'Bút Máy Điểm 10 FT-021 Plus - Màu Vàng', 40375, 'Bút Máy Điểm 10 FT-021 Plus là sản phẩm đang được rất nhiều em học sinh, nhất là học sinh tiểu học yêu thích trong việc rèn chữ đẹp.\r\n\r\nĐầu ngòi viết ổn định, chất lượng, dễ dàng viết được nét thanh nét đậm. Trọng lượng bút nhẹ, vừa tay cầm, đảm bảo cho các em học sinh có thể kiên trì tạo nên những nét chữ đẹp như ý muốn.\r\n\r\nThân bút được sơn bóng đẹp mắt. Dắt viết kim loại mạ bạc tạo sự sang trọng và khác biệt với các loại bút máy thông thường trên thị trường.\r\n\r\nNgòi bút được làm bằng thép cao cấp, bền bỉ, đầu ngòi bút có hạt Iridium viết trơn, nét nhỏ, dùng để tập viết, là ngươi bạn đáng tin cậy mang đến những nét thanh nét đậm đẹp như mong muốn.', '/assets/images/butmay020_1.jpg', '/assets/images/butmay020_2.jpg', 3, 30),
(21, 'Bút Máy Điểm 10 FT-020 Plus (Tặng Ngòi FPN-07) - Màu Hồng', 68400, 'Bút Máy Điểm 10 FT-020 Plus (Tặng Ngòi FPN-07)\r\n\r\n- Dòng bút máy không mài ngòi, ngòi bút bằng thép cao cấp, bền. Đầu ngòi có hạt Iridium siêu bền, viết trơn, nét nhỏ, rất thích hợp cho học sinh bắt đầu tập viết và khách hàng có nhu cầu dùng bút ngòi trơn, viết nhanh.\r\n\r\n- Thân bút bằng kim loại, khá đầm tay khi cầm viết.\r\n\r\n- Tảm bút được thiết kế định vị tay cầm giúp cầm viết dễ dàng , không trơn, không mỏi tay khi viết.\r\n\r\n- Hộp cao cấp in 4 màu đẹp mắt, đựng 1 cây sang trọng và dễ trưng bày.\r\n\r\n- Thân bút có 2 màu: xanh, hồng\r\n\r\n- Tặng kèm 01 ngòi FPN-07', '/assets/images/butmay021_1.jpg', '/assets/images/butmay021_2.jpg', 3, 30),
(22, 'Bút Lông Kim Artline Supreme EPFS-200 - 0.4mm - Màu Đen', 19950, 'Bút Lông Kim Artline Supreme EPFS-200 - 0.4mm - Màu Đen là dòng bút dành riêng cho các bạn thích vẽ mỹ thuật, vẽ phát họa hay anime. Màu bút rất đẹp và sáng. Ngòi bút viết rất êm, mực ra đều mang lại trãi nghiệm tuyệt vời cho người sử dụng.\r\n\r\nDòng bút lông kim Supreme đang làm mưa làm gió trên thị trường văn phòng phẩm thế giới của Artline Japan.\r\n\r\nBút được thiết kế lấy cảm hứng từ cây bút chì gỗ với thân bút dài mảnh, mang lại cảm giác thân thuộc khi sử dụng. Thân bút hình tam giác đều được vót 3 cạnh tạo hình lúc 3 ngón tay chụm lại. Bút sử dụng ngòi bằng sợi Polyester cao cấp giúp mực thấm ra đều và viết cực kỳ êm.\r\n\r\nNgòi bút 0.4mm, sử dụng được cho nhiều mục đích từ viết đến vẽ. Bút có đến 20 màu giúp cho các bạn yêu thích mỹ thuật có thể sử dụng bút kim Artline để vẽ nét, vẽ phát thảo hay tô màu.', '/assets/images/butlong022_1.jpg', '/assets/images/butlong022_2.jpg', 4, 30),
(23, 'Bút Lông Kim FL-04/DO - Mực Tím', 7280, 'Bút Lông Kim FL-04/DO - Mực Tím\r\n\r\nĐầu bi 0.3mm, bọc kim loại.\r\n\r\nLượng mực viết dài 1200-1500m, không bị lem và đều\r\n\r\nNét viết thanh mãnh nhỏ, trơn, êm\r\n\r\nDáng bút thon gọn dễ cầm , xinh xắn thích hợp với trẻ em.\r\n\r\nThân in hình Doraemon rất được các bạn trẻ yêu mến và sinh động tạo sự thích thú cho các em rèn luyện chữ viết', '/assets/images/butlong023_1.jpg', '/assets/images/butlong023_2.jpg', 4, 30),
(24, 'Bút Lông Kim 4600 Marvy 0.05', 26600, 'Bút Vẽ Kỹ Thuật Marvy - 4600 thường được dùng trong vẽ, thiết kế, kiến trúc, vẽ truyện tranh... Đây là một trong những sản phẩm được ưa chuộng nhất tại Nhật Bản và các nước Châu Á. Bạn có thể sử dụng với nhiều lĩnh vực và công việc khác nhau.\r\nThiết kế đẹp\r\nBút nhỏ gọn vừa tay, cầm bút viết rất nhẹ và êm tay. Mực ít bị thấm trên giấy mỏng, khi viết mực ra đều và êm, rất nét, viết trơn không bị cào giấy.\r\nChất lượng đảm bảo và an toàn\r\nChất liệu vỏ nhựa, ngòi kim, mực đảm bảo độ bền. Màu mực bám tốt, hạn chế lem, phai.\r\nBút không chứa chất độc hại: Xylene và Toluene, đảm bảo an toàn với sức khỏe người tiêu dùng.\r\nSize ngòi:0.05\r\nMàu mực: Đen.', '/assets/images/butlong024_1.jpg', '/assets/images/butlong024_1.jpg', 4, 30),
(25, 'Bút Lông Kim Artline Supreme EPFS-200 - 0.4mm - Đỏ Đậm', 19950, 'Bút Lông Kim Artline Supreme EPFS-200 - 0.4mm - Đỏ Đậm gây ấn tượng bởi thiết kế vô cùng đẹp mắt, tinh tế trong cách phối hợp giữa màu sắc và kiểu dáng, tạo cảm giác thật thoải mái cho bạn khi sử dụng.\r\n\r\nLà loại bút dành cho học sinh, sinh viên hay giới văn phòng, đặc biệt là những bạn yêu thích vẽ. Thân bút được cấu tạo hình tam giác đều giúp cho việc cầm thoải mái không gây mỏi tay.\r\nNgòi bút 0.4mm viết mềm, tạo nét mịn, mượt và không cần phải sử dụng nhiều lực ấn. Mực không chứa chất độc hại, đảm bảo cho sức khỏe người tiêu dùng.\r\n\r\nMực có màu đỏ đậm, viết lâu phai, không xóa được, độ bền màu cao. Đặc biệt, mực không bị khô và có thể viết bình thường dù không được đậy nắp trong vài ngày.', '/assets/images/butlong025_1.jpg', '/assets/images/butlong025_2.jpg', 4, 30),
(26, 'Artline Supreme Brush Bút Lông Kim - Màu Mơ VEPF-FAPR', 19950, 'Artline Supreme Brush Bút Lông Kim - Màu Mơ VEPF-FAPR\r\n\r\nBút lông Artline Supreme có màu sắc phong phú, tươi mới dễ sử dụng giúp bạn đa dạng hóa lựa chọn. Bạn còn có thể blend màu – pha trộn màu với nhau.\r\n\r\nĐầu lông cao cấp cho nét bút cực kỳ quyến rũ. Nếu bạn thích viết thư pháp hiện đại bằng bút lông thì không nên bỏ lỡ bộ bút này.\r\n\r\nChất mực vẽ của bút lông Artline Supreme đặc biệt nhiều và trong sáng. thích hợp dùng trong mỹ thuật, kiến trúc hay cho bé tô, vẽ thay cho nước.\r\n\r\nVới chất lượng tuyệt vời,  Artline Supreme Brush Maker quả là một dòng bút vẽ đáng để bạn đầu tư. Hãy cùng bút lông Artline Supreme  trải nghiệm những thú vị khi viết thư pháp bạn nhé.', '/assets/images/butlong026_1.jpg', '/assets/images/butlong026_1.jpg', 4, 30),
(27, 'Bút Lông Kim EK-220 BL - Mực Xanh', 21850, 'Bút Lông Kim EK-220 BL - Mực Xanh\r\n\r\nArtline 220 có đầu nhựa cao cấp nên nó hoàn hảo để sử dụng với giấy nến và giấy viết chữ. Mực khô và mờ ngay lập tức cho kết quả tức thì. Bút lý tưởng cho công việc viết chính xác.\r\n\r\nMực nước khô ngay lập tức nên có khả năng ngăn chặn vết bẩn . Mực sẽ không lem qua giấy để bạn có thể viết trên cả hai mặt của mỗi trang.', '/assets/images/butlong027_1.jpg', '/assets/images/butlong027_1.jpg', 4, 30),
(28, 'Bút Ghi Nhớ Memo Pen - MMP-OR-01', 14850, 'Bút Ghi Nhớ Memo Pen MMP-OR-01\r\n\r\nBút ghi nhớ được sử dụng trong Bộ Dụng Cụ Ghi Nhớ - đây là một bộ công cụ trợ giúp tuyệt vời mà các bạn học sinh Nhật thường sử dụng. Khi sử dụng Bộ Dụng Cụ Ghi Nhớ mà bút hết mực, bạn có thể mua Bút Ghi Nhớ Memo Pen MMP-OR-01 để có thể sử dụng tiếp.\r\n\r\nCách thức sử dụng rất đơn giản. Viết câu trả lời/ý cần nhớ bằng Bút Ghi Nhớ Memo Pen MMP-OR-01 - cây bút màu cam đặc biệt, sau đó sử dụng tấm phin màu đỏ để che khu vực cần học thuộc (tấm phin trong Bộ Dụng Cụ Ghi Nhớ). Phần câu chữ được viết bằng mực đen hoặc màu tối vẫn có thể đọc được, nhưng phần mực màu cam sẽ hoàn toàn biến mất, cho phép bạn tự kiểm tra chính xác các câu trả lời/ý cần nhớ.', '/assets/images/butlong028_1.jpg', '/assets/images/butlong028_2.jpg', 4, 30),
(29, 'Chì Bấm 0.5 M&G AMP840X9 - Bút Trắng Nai Xám', 11700, 'Chì Bấm 0.5 M&G AMP840X9 là công cụ hỗ trợ tuyệt vời cho việc ghi chép mà còn thể hiện được cá tính riêng của bạn.\r\n\r\nBút chì bấm được thiết kế từ chất liệu nhựa tổng hợp cao cấp nên thân thiện với môi trường, bảo vệ tối đa sức khỏe người sử dụng.\r\n\r\nSản phẩm sử dụng ngòi 0.5 mm; có thanh gài chắc chắn đem đến sự tiện lợi cho người dùng khi có thể dễ dàng mang bút theo bên mình bằng cách gài lên túi áo.\r\n\r\nĐặc biệt, bút bấm rất êm, cho ra lượng ngòi vừa phải, hạn chế tối đa sự gãy ngòi, tiết kiệm nhiều hơn cho bạn.', '/assets/images/chibam029_1.jpg', '/assets/images/chibam029_2.jpg', 5, 30),
(30, 'Bút Chì Bấm GuangBo 0,5mm GUBB14003D - Màu Hồng', 14500, 'Bút Chì Bấm GuangBo 0,5mm GUBB14003D\r\n\r\nBút chì bấm được làm từ chất liệu nhựa tổng hợp có khả năng chịu lực tốt, hạn chế nứt, vỡ khi rơi.\r\n\r\nChất liệu nhựa còn có trọng lượng nhẹ, không gây mỏi khi sử dụng lâu.\r\n\r\nBút bấm êm, cho ra lượng ngòi vừa phải, hạn chế gãy ngòi.\r\n\r\nSử dụng min chì 0.5 mm, cho nét viết thanh mãnh.\r\n\r\nĐầu bút với thiết kế quai cài tiện lợi, bạn có thể kẹp vào bìa sách, vở hay túi quần áo để tránh khả năng rơi rớt.\r\n\r\nSản phẩm phù hợp với mọi lứa tuổi như đi học, đi làm..v.v....', '/assets/images/chibam030_1.jpg', '/assets/images/chibam030_2.jpg', 5, 30),
(31, 'Bút Chì Bấm QuanTum QM224 - Màu Hồng', 42275, 'Bút Chì Bấm QuanTum QM224\r\n\r\nSẽ mang đến cho khách hàng sự hài lòng tuyệt đối về cả thiết kế lẫn chất lượng. Sự kết hợp hoàn hảo giữa kiểu dáng tinh tế và những tính năng vượt trội khiến mẫu bút này trở thành một trong những lựa chọn hàng đầu cho loại bút chì tốt nhất. Đây chắc chắn là sản phẩm mà bạn không thể bỏ qua nếu đang tìm kiếm một công cụ hỗ trợ tuyệt vời cho công việc và học tập', '/assets/images/chibam031_1.jpg', '/assets/images/chibam031_2.jpg', 5, 30),
(32, 'Bút Chì Bấm QuanTum QM224 - Màu Vàng', 42275, 'Bút Chì Bấm QuanTum QM224\r\n\r\nSẽ mang đến cho khách hàng sự hài lòng tuyệt đối về cả thiết kế lẫn chất lượng. Sự kết hợp hoàn hảo giữa kiểu dáng tinh tế và những tính năng vượt trội khiến mẫu bút này trở thành một trong những lựa chọn hàng đầu cho loại bút chì tốt nhất. Đây chắc chắn là sản phẩm mà bạn không thể bỏ qua nếu đang tìm kiếm một công cụ hỗ trợ tuyệt vời cho công việc và học tập', '/assets/images/chibam032_1.jpg', '/assets/images/chibam032_2.jpg', 5, 30),
(33, 'Bút Chì Kim Nắp Đậy (mới) - Xanh-Pentel - A105C_S', 29925, 'Bút Chì Kim Nắp Đậy (mới) - Xanh-Pentel - A105C_S\r\n\r\nSở hữu kiểu dáng rất trang nhã và tinh tế. Thiết kế thon dài thanh mảnh kết hợp hoàn hảo với nghệ thuật phối màu mang lại cho sản phẩm một phong cách nhẹ nhàng, thanh lịch. Cùng với chất lượng vượt trội, bút chì thân kim trong không những là công cụ hỗ trợ tuyệt vời cho việc ghi chép mà còn thể hiện được cá tính riêng của bạn.', '/assets/images/chibam033_1.jpg', '/assets/images/chibam033_2.jpg', 5, 30),
(34, 'Bút Chì Kim Pentel PD275T- Màu Đen', 55460, 'Bút Chì Kim Pentel PD275T - Bút chì bấm hiện nay đã trở thành một công cụ hỗ trợ không thể thiếu đối với mọi người. Một cây bút tốt sẽ giúp các bạn rất nhiều trong học tập và công việc. Với thiết kế tinh tế, chất lượng vượt trội, sản phẩm bút chì kim của Pentel sẽ là người bạn đồng hành đáng tin cậy của bạn.', '/assets/images/chibam034_1.jpg', '/assets/images/chibam034_2.jpg', 5, 30),
(35, 'Chì Bấm G-Star Mp 07 - Hồng', 6300, 'Chì Bấm G-Star Mp 07 có thiết kế dạng vặn bấm ngòi tiện lợi, giúp bạn thay đối ngoì, sử dụng trong thời gian dài. Sản phẩm có thiết kế bắt mắt, thích hợp với nhiều đối tượng học sinh, sinh viên. Thân bút được làm bằng chất liệu nhựa bền bỉ, không dễ vỡ nứt khi gặp va chạm mạnh.', '/assets/images/chibam035_1.jpg', '/assets/images/chibam035_2.jpg', 5, 30),
(36, 'Vỉ 6 Cây Bút Dạ Quang Crown F-500 Set', 43700, 'Vỉ 6 Cây Bút Dạ Quang Crown F-500 Set là sản phẩm chuyên dụng làm nổi bật những thông tin quan trọng, giúp bạn đọc tài liệu một cách dễ dàng. Bộ sản phẩm gồm 6 màu sắc thông dụng, tiện lợi cho người sử dụng. Sản phẩm thích hợp cho nhiều đối tượng như: nhân viên văn phòng, học sinh, sinh viên.', '/assets/images/daquang036_1.jpg', '/assets/images/daquang036_2.jpg', 6, 30),
(37, 'Vỉ 6 Cây Viết Dạ Quang Xuczi BC-701', 27000, 'Vỉ 6 Cây Viết Dạ Quang Xuczi BC-701\r\n\r\nMàu sắc nổi bật - Sở hữu thiết kế thông minh, bút cầm khá chắc tay và dễ sử dụng giúp đánh dấu những phần quan trọng, cho bạn ghi nhớ dễ dàng.\r\n\r\nBút có 6 màu cho bạn nhiều lựa chọn: vàng, xanh lá, xanh ngọc, cam, tím, hồng phục vụ nhu cầu sử dụng đa dạng của bạn, vừa có thể trang trí, đánh dấu và có khả năng phát quang nên giúp ích rất nhiều cho bạn.\r\n\r\nAn toàn, tiết kiệm - Bút có thể bơm mực giúp tiết kiệm chi phí, mực dạng nước dùng cho giấy thường, giấy đặc biệt không để lại dấu vết khi muốn photo, fax vô cùng tiện lợi.\r\n\r\nSử dụng bởi các nguyên liệu tốt cho môi trường. Mực không chứa độc tố nên rất an toàn cho sức khỏe người tiêu dùng.\r\n\r\nNhỏ gọn dễ mang theo - Thiết kế hoa văn hợp thời trang, phù hợp cho học sinh, sinh viên. Nhỏ gọn, có kẹp dắt túi. - Nhìn sáng bóng, thiết kế kẻ sọc. - Phần tay cầm không trơn.', '/assets/images/daquang037_1.jpg', '/assets/images/daquang037_2.jpg', 6, 30),
(38, 'Vỉ 6 Bút Dạ Quang Marvy 55', 57000, 'Vỉ Bút Dạ Quang Marvy 50 \r\n\r\nLà một trong những sản phẩm bút viết không thể thiếu của giới sinh viên, học sinh hay văn phòng...\r\nBút có màu sắc bắt mắt giúp bạn làm nổi bật chủ đề muốn nhấn mạnh. Chất liệu mực an toàn cho người dùng, màu mực ra đều và nhanh, tránh lem hay ướt trang giấy.\r\nKiểu dáng nhỏ gọn, phần tay cầm không trơn giúp dễ dàng khi sử dụng.\r\nMực dạng nước phù hợp cho các loại giấy: giấy thường, giấy copy, giấy fax.\r\nThiết kế đầu lớn, mực sáng, không bị đen khi sử dụng.', '/assets/images/daquang038_1.jpg', '/assets/images/daquang038_2.jpg', 6, 30),
(39, 'Vỉ 4 Bút Dạ Quang 2 Đầu VHL105 + Tặng 1 Bút Chì', 25850, 'Vỉ 4 Bút Dạ Quang 2 Đầu VHL105 + Tặng 1 Bút Chì\r\n\r\nSản phẩm gồm 4 bút có màu nổi bật, sở hữu thiết kế tiện dụng với hai đầu: đầu tròn và đầu dẹp giúp bạn có thể ghi chú, đánh dấu thông tin dễ dàng. \r\n\r\nSản phẩm có kiểu dáng nhỏ gọn, tiện dụng phần tay cầm vừa vặn giúp bạn cầm bút thoải mái chắc chắn tránh tình trạng bút trơn tuột, đau tay khi sử dụng. Đặc biệt nắp đậy chắc chắn bảo vệ ngòi bút tối ưu hơn.\r\n\r\nBên cạnh đó, màu mực tươi sáng, nhanh khô. Độ phản quang cao làm nổi bật các dòng, các điểm cần lưu ý trong văn bản. \r\n\r\nĐặc biệt, bút phù hợp với các loại giấy: giấy thường, giấy copy, giấy fax, không làm nhòe và tô đen nội dung khi fax hay photocopy.\r\n\r\nNgoài ra, mực viết được sản xuất từ chất liệu cao cấp không gây mùi khó chịu, không độc hại, đạt tiêu chuẩn.', '/assets/images/daquang039_1.jpg', '/assets/images/daquang039_2.jpg', 6, 30),
(40, 'Vỉ 2 Cây Bút Dạ Quang FO-HL01 - Màu Hồng', 15810, 'Vỉ 2 Cây Bút Dạ Quang FO-HL01 - Màu Hồng\r\n\r\nBút dạ quang FO-HL01 là sản phẩm mới thuộc dòng sản phẩm Officemate series do Tập đoàn Thiên Long sản xuất, mang nhãn hiệu Flexoffice.\r\n\r\nĐẶC ĐIỂM NỔI BẬT :\r\n- Bút dạ quang FO-HL01 có 2 đầu bút: Một đầu nhỏ và một đầu lớn, giúp đa dạng nét viết, thuận tiện khi sử dụng. Sản phẩm được sản xuất theo công nghệ hiện đại, đạt tiêu chuẩn chất lượng quốc tế.\r\n- Kiểu dáng thon gọn, trẻ trung Màu dạ quang mạnh, không làm lem nét chữ của mực khi viết chồng lên và không để lại vết khi qua photocopy đây là đặt điểm vượt trội của bút dạ quang FO-HL01.', '/assets/images/daquang040_1.jpg', '/assets/images/daquang040_2.jpg', 6, 30),
(41, 'Vỉ 6 Cây Viết Dạ Quang Xuczi BC-704', 35100, 'Vỉ 6 Cây Viết Dạ Quang Xuczi BC-704\r\n\r\nVỉ gồm 6 bút dạ quang hình bò sữa rất cute, gồm có 6 màu: cam, hồng, tím, xanh lam, xanh dương, vàng\r\n\r\nPhù hợp cho vẽ màu nước, làm thiệp.\r\n\r\nTô màu siêu mịn mang đến trải nghiệm tô màu dễ dàng. Mực khô nhanh, không phai và dễ lau sạch, phù hợp cho trẻ em.\r\n\r\nSản phẩm hữu ích và tiện lợi cho trường học và văn phòng,\r\n\r\nNhựa chất lượng cao, đẹp và bền. Một ý tưởng quà tặng độc đáo cho học sinh và trẻ em. ', '/assets/images/daquang041_1.jpg', '/assets/images/daquang041_2.jpg', 6, 30),
(42, 'Vỉ 6 Cây Bút Dạ Quang 6507', 38250, 'Vỉ 6 Cây Bút Dạ Quang 6507\r\n\r\nVỉ 6 bút dạ quang xinh xắn nhỏ nhắn trong cùng 1 bộ, không chỉ là một bộ dụng cụ học tập, văn phòng mà còn giúp chúng ta tạo hứng khởi trong học tập và làm việc.\r\n\r\nBộ bút dạ quang có 6 màu cho bạn nhiều lựa chọn: vàng, xanh lá, xanh ngọc, cam, tím, hồng phục vụ nhu cầu sử dụng đa dạng của bạn, vừa có thể trang trí, đánh dấu và có khả năng phát quang nên giúp ích rất nhiều cho bạn.\r\n\r\nGiúp công việc của bạn sẽ suôn sẻ, trôi chảy hơn.\r\n\r\nSở hữu thiết kế thông minh, bút cầm khá chắc tay và dễ sử dụng giúp đánh dấu những phần quan trọng, cho bạn ghi nhớ dễ dàng.\r\n\r\nSử dụng bởi các nguyên liệu tốt cho môi trường. Mực không chứa độc tố nên rất an toàn cho sức khỏe người tiêu dùng.', '/assets/images/daquang042_1.jpg', '/assets/images/daquang042_2.jpg', 6, 30),
(43, 'Ba Lô Fancy - Bumblebee Dũng Mãnh - BT1106', 568650, 'Ba Lô Fancy - Bumblebee Dũng Mãnh - BT1106\r\n\r\n- Sản phẩm siêu nhẹ và chống gù.\r\n\r\n- Sử dụng nguyên phụ liệu an toàn, có nguồn gốc rõ ràng (tuyệt đối không có mùi hôi khó chịu).\r\n\r\n- Có băng phản quang.\r\n\r\n- Có quai đeo ngực trợ lực.\r\n\r\n- Kết cấu cân bằng, phù hợp với vóc dáng của trẻ em Việt Nam.\r\n\r\n- Ba Lô Chống Gù Thế Hệ Mới với chủ đề Tranformer cùng nhân vật Bumblebee dũng mãnh dập nổi 3D ấn tượng.', '/assets/images/balotrai043_1.jpg', '/assets/images/balotrai043_2.jpg', 7, 30),
